mapper.insert:
with upsert as 
(
	update tbl_svrlst set 
		svr_nm = #{svr_nm}
		,svr_ip = #{svr_ip}
		,svr_port = #{svr_port}
	where
		seq = #{seq}
	returning *
)
insert into tbl_svrlst
(
	svr_nm,
	svr_ip,
	svr_port
)
select 
	#{svr_nm},
	#{svr_ip},
	#{svr_port}
where not exists(select * from upsert);

mapper.update:
update 
	tbl_svrlst
set	
	svr_used = #{svr_used}
where 
	svr_nm = #{svr_nm}
